css:
body {
    font-family: Arial, sans-serif;
    font-size: 14px;
}

.title {
    background-color: #0B8CDA; /* Retained color */
    color: white;
    text-align: center;
    padding: 10px;
    border-radius: 2px;
    font-size: 24px;
}

.section-title {
    background-color: #18BEEC; /* Retained color */
    color: white;
    text-align: center;
    padding: 5px;
    border-radius: 2px;
    font-size: 18px;
    margin-top: 20px;
}

.details-table, .modules-table, .hours-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
    border: 1px solid black; /* Added border to ensure table structure is visible */
}

.details-table td, .modules-table td, .hours-table td, .hours-table th {
    border: 1px solid black; /* Ensured consistent border for all cells */
    padding: 8px;
}

.hours-table th.text-center, .hours-table td.text-center {
    text-align: center; /* Centers text for specific columns */
}

@media print {
    .print-format {
        margin: 0mm;
    }

    * {
        font-size: 14px;
    }

    .title {
        background-color: #0B8CDA; 
        color: white;
    }

    .section-title {
        background-color: #18BEEC; 
        color: white;
    }

    .details-table, .modules-table, .hours-table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 10px;
        border: 1px solid black; /* Ensured border for print */
    }

    .details-table td, .modules-table td, .hours-table td, .hours-table th {
        border: 1px solid black; /* Consistent border for print */
        padding: 8px;
    }
}
.hours-table thead th {
    font-weight: bold; 
    color: black; 
    background-color: #f2f2f2;
}
