css:
body {
    font-family: Arial, sans-serif; /* Setting the font to Arial */
    font-size: 14px; /* Setting the base font size to 14px */
    color: #333333; /* Setting the text color to dark grey */
    margin: 0; /* Removing default margin */
    padding: 20px; /* Adding padding around the body needed or not???*/
    background-color: #f7f7f7; /* Setting the background color to light grey needed or not??? */
}


header {
    background-color: #333333; /* Setting the background color to dark grey */
    color: white; /* Setting the text color to white */
    text-align: center; /* Centers the text */
    padding: 10px; /*  padding inside the header */
    border-radius: 4px; /* Rounds the corners of the header */
}


.title {
    font-size: 24px; /* Setting the font size to 24px */
    font-weight: bold; /* Making the font bold */
    margin: 0; /* Removing default margin */
}


.section-title {
    color: black; /* Setting the text color to black */
    text-align: center; 
    padding: 10px; /* Adding padding inside the section title */
    border: 1px solid #cccccc; /* Adding a light grey border */
    border-radius: 4px; /* Round corner */
    font-size: 18px; /* Setting the font size to 18px */
    margin-top: 20px; /* Adding a margin above the section title */
    background-color: #ffffff; /* Setting the background color to white */
}


.details-table, .hours-table {
    width: 100%; /* Setting the table width to 100% */
    margin-top: 10px; /* Adding a margin above the tables */
    background-color: white; /* Setting the background color to white */
    border-collapse: collapse; /* borders are not double */
}


.details-table th, .hours-table th {
    border: 1px solid #cccccc; /* Adding a light grey border */
    padding: 10px; /* padding inside the cells */
    background-color: #f2f2f2; /* Setting the background color to light grey */
    font-weight: bold; /* Making the font bold */
    text-align: center; /* Centers the text */
}

/* Styling for table cells */
.details-table td, .hours-table td {
    border: 1px solid #cccccc; /* Adding a light grey border */
    padding: 10px; /* Adding padding inside the cells */
    background-color: white; /* Setting the background color to white */
}

.hours-table tbody tr:nth-child(even) {
    background-color: #f9f9f9; /* Setting the background color to light grey */
}

.hours-table tbody tr:nth-child(odd) {
    background-color: white; /* Setting the background color to white */
}

.text-center {
    text-align: center; /* Centers the text */
}
