css:
      body {
          font-family: 'Inter', sans-serif;
          margin: 0;
          padding: 20px;
          color: #333;
      }

      .container {
          background-color: #fff;
          padding: 20px;
          border-radius: 8px;
          box-shadow: 0 0 10px rgba(0,0,0,0.1);
          max-width: 900px;
          margin: auto;
      }

      header {
          text-align: center;
          margin-bottom: 20px;
      }

      h1 {
          margin: 0;
          font-size: 28px;
          color: #333;
          text-align : center;
      }

      .header-content {
          display: grid;
          grid-template-columns: 1fr 1fr;
          margin-top: 20px;
      }

      .left-align {
          text-align: left;
      }

      .right-align {
          text-align: right;
      }

      .header-content p {
          margin: 0;
      }

      .efforts-table {
          width: 100%;
          border-collapse: collapse;
          margin: 20px 0;
          font-size: 14px;
          color: #333;
      }

      .efforts-table td {
          /*border: 1px solid #ddd;*/
          padding: 10px;
      }

      .efforts-table th {
          background-color: #f9f9f9;
          border: 1px solid #ddd;
          padding: 10px;
      }

      .value {
          font-size: 1.2em;
          font-weight: bold;
          color: #007bff;
      }

      .separator {
          width: 1px;
          background-color: lightgrey;
          height: 50px;
      }

  /*    .separator {*/
  /*        width : 1px;*/
      /*border-left: 1px solid black;*/
  /*}*/

      .center-align {
          text-align: center;
      }

      .center-align .value {
          text-align: center;
      }

      .module-wise-estimate-table {
          width: 100%;
          border: 1px solid #ddd;
          border-collapse: collapse;
          font-size: 14px;
          margin-bottom: 20px;
      }

      .module-wise-estimate-table th,
      .module-wise-estimate-table td {
          border: 1px solid #ddd;
          padding: 8px;
          text-align: left;
      }

      .module-wise-estimate-table th {
          background-color: #f9f9f9;
          font-weight: bold;
      }

      .left-align p,
      .right-align p,
      .center-align p {
          margin: 0 0 10px 0;
      }

      .left-align p:last-child,
      .right-align p:last-child,
      .center-align p:last-child {
          margin-bottom: 0;
      }



table {
      width: 100%;
      border-collapse: collapse;
  }
  th, td {
      padding: 8px;
  }
  .left {
      text-align: left;
  }
  .right {
      text-align: right;
  } 


  .vertical {
          border-left: 1px solid lightgrey;
          height: 50px;
      }
